pragma solidity 0.6.4;

library Shared {

    /**
     *   Extract PublishersId from AssetId
     */
    function extractPublisher(bytes32 source) public pure returns (bytes8) {
        bytes8 out;
        for (uint i = 0; i < 8; i++) {
            out |= bytes8(source[i] & 0xFF) >> (i * 8);
        }
        return out;
    }

    /**
     *   Extract Root-ID from AssetId
     */
    function extractRootId(bytes32 source) public pure returns (bytes32) {
        bytes32 out;
        for (uint i = 0; i < 28; i++) {
            out |= bytes32(source[i] & 0xFF) >> (i * 8);
        }
        return out;
    }

    /**
     *  Extract Index from NFA AssetId
     *  (Last 4 Bytes)
     */
    function extractIndex(bytes32 source) public pure returns (bytes4) {
        bytes4 out;
        for (uint i = 28; i < 32; i++) {
            out |= bytes4(source[i] & 0xFF) >> (i * 8);
        }
        return out;
    }

    /**
     *  Attach Index to Root-Assets ID
     */
    function attachIndex(bytes32 assetId, uint32 index) public pure returns (bytes32) {
        bytes32 out;
        bytes4 bytesIndex = bytes4(index);
        for (uint i = 0; i < 32; i++) {
            if (i < 28) {
                out |= bytes32(assetId[i] & 0xFF) >> (i * 8);
            } else {
                out |= bytes32(bytesIndex[i-28] & 0xFF) >> (i * 8);
            }
        }
        return out;
    }

    function isValidHash(bytes memory b) public pure returns (bool) {
        if(b.length != 64) return false;

        for (uint i = 0; i<64; i++) {
            if (b[i] < "0") return false;
            if (b[i] > "9" && b[i] < "a") return false;
            if (b[i] > "f") return false;
        }

        return true;
    }


}
